import {HYDRATE} from 'next-redux-wrapper';


export const initialState={
    isLoggedIn:false,
    user:null,
    signUpData:{},
    loginData:{}
}

export const reducer = (state=initialState,action)=>{

    switch(action.type){
        case HYDRATE:
            console.log('HYDRATE',action)
            return{...state,...action.payload}
            case 'LOG_IN':
                return {
                       ...state,
                       isLoggedIn:true,
                       user:action.data

                };
            case 'LOG_OUT':
                return {
                        ...state,
                        isLoggedIn:false,
                        user:null
                }  
        default:
            return state;
    }

}

export default reducer
