import {HYDRATE} from 'next-redux-wrapper';
import shortId from 'shortid'


export const initialState={
    mainPosts:[{
        id:1,
        User:{
            id:1,
            nickname:'동영'
        },
        content:'첫 번째 게시글 #해시태그 #익스프레스',
        Images:[{
            src:'https://helpx.adobe.com/content/dam/help/en/photoshop/using/quick-actions/remove-background-before-qa1.png'
        },{
            src:'https://byline.network/wp-content/uploads/2018/05/cat.png'
        }

        ],
        Comments:[
            {
             User:{
                 nickname:'ji'
             },
             content:'사고싶어요'
            }
        ]

    }],
    imagePaths:[],
    addpostLoading:false,
    addpostDone:false,
    addpostError:null,

    removepostLoading:false,
    removepostDone:false,
    removepostError:null,

    addcommentLoading:false,
    addcommentDone:false,
    addcommentError:null
}

const dummyPost=(id,content)=>{
    return{
        id,
        content,
        User:{
            id:1,
            nickname:'제로'
        },
        Images:[],
        Comments:[]
    }

}

const dummyComment=(data)=>{
    return{
            id:data.id,
            User:{
                nickname:'ji'
            },
            content:data.content
           }

}
export const ADD_POST_REQUEST='ADD_POST_REQUEST'; 
export const ADD_POST_SUCCESS='ADD_POST_SUCCESS';
export const ADD_POST_FAILURE='ADD_POST_FAILURE';

export const ADD_COMMENT_REQUEST='ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS='ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE='ADD_COMMENT_FAILURE';

export const REMOVE_POST_REQUEST='REMOVE_POST_REQUEST';
export const REMOVE_POST_SUCCESS='REMOVE_POST_SUCCESS';
export const REMOVE_POST_FAILURE='REMOVE_POST_FAILURE';



export const addpostRequestAction=(data)=>{
   return{
    type:ADD_POST_REQUEST,
    data
   }
}

export const addcommentRequestAction=(data)=>{
    return{
        type:ADD_COMMENT_REQUEST,
        data
    }
}

export const reducer = (state=initialState,action)=>{

    switch(action.type){

        case HYDRATE:
            console.log('HYDRATE',action)
            return{...state,...action.payload}

        case ADD_POST_REQUEST:
            return{
                ...state,
                addpostDone:false,
                addpostLoading:true,
                addpostError:null
            }    
        case ADD_POST_SUCCESS:
            return{
                ...state,
                mainPosts:[dummyPost(action.data.id,action.data.content),...state.mainPosts],
                addpostDone:true,
                addpostLoading:false,
            }
        case ADD_POST_FAILURE:
            return{
                ...state,
                addpostDone:false,
                addpostLoading:false,
                addpostError:action.err
            }
            case ADD_COMMENT_REQUEST:
                return{
                    ...state,
                    addcommentLoading:true,
                    addcommentDone:false,
                    addcommentError:null
                }    
            case ADD_COMMENT_SUCCESS:{

                console.log('성공',action.data)
                const postIndex =state.mainPosts.findIndex((v)=>v.id===action.data.postId);
                const post ={...state.mainPosts[postIndex]};
                post.Comments=[dummyComment(action.data.content),...post.Comments];
                const mainPosts=[...state.mainPosts];
                mainPosts[postIndex]=post
                return{
                    ...state,
                    mainPosts,
                    addcommentDone:true,
                    addcommentLoading:false,
                }
            }
            case ADD_COMMENT_FAILURE:
                return{
                    ...state,
                    addcommentDone:false,
                    addcommentLoading:false,
                    addcommentError:action.err
                }
            case REMOVE_POST_REQUEST:
                    return{
                        ...state,
                        removepostDone:false,
                        removepostLoading:true,
                        removepostError:null
                    }    
            case REMOVE_POST_SUCCESS:{

                const newmainpost=state.mainPosts.filter((m)=>m.id!==action.data)
                console.log('asd')
                    return{
                        ...state,

                        mainPosts:newmainpost,



                        removepostDone:true,
                        removepostLoading:false,
                    }
                }
            case REMOVE_POST_FAILURE:
                    return{
                        ...state,
                        removepostDone:false,
                        removepostLoading:false,
                        removepostError:action.err
                    }
        default:
            return state;
    }

}

export default reducer
