import {HYDRATE} from 'next-redux-wrapper';





export const initialState={
    loginLoading:false,
    loginDone:false,
    loginError:null,

    logoutLoading:false,
    logoutDone:false,
    logoutError:null,

    signupLoading:false,
    signupDone:false,
    signupError:null,

    nicknameLoading:false,
    nicknameDone:false,
    nicknameError:null,

    me:null,
    signUpData:{},
    loginData:{}
}


const dummyUser=(data)=>{
return{
    ...data,
    nickname:'zerodong',
    id:1,
    Posts:[],
    Followings:[],
    Followers:[]

}
}

export const NICKNAME_REQUEST = 'NICKNAME_REQUEST';
export const NICKNAME_SUCCESS='NICKNAME_SUCCESS'
export const NICKNAME_FAILURE = 'NICKNAME_FAILURE';

export const SIGN_UP_REQUEST = 'SIGN_UP_REQUEST';
export const SIGN_UP_FAILURE='SIGN_UP_FAILURE'
export const SIGN_UP_SUCCESS = 'SIGN_UP_SUCCESS';

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST'; // 액션의 이름
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS'; // 액션의 이름
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'; // 액션의 이름

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_FAILURE='LOG_OUT_FAILURE'
export const LOG_OUT_SUCCESS='LOG_OUT_SUCCESS'

export const ADD_POST_TO_ME='ADD_POST_TO_ME'
export const REMOVE_POST_TO_ME='REMOVE_POST_TO_ME'


export const nicknameRequestAction=(data)=>{
    return{
        type:NICKNAME_REQUEST,
        data
    }
}

export const loginRequestAction = (data) => {
    return {
      type: LOG_IN_REQUEST,
      data,
    }
  };
  export const logoutRequestAction =()=> {
      return{
        type: LOG_OUT_REQUEST,

      }
  };
  export const signupRequestAction=(data)=>{
      return{
          type:SIGN_UP_REQUEST,
          data
      }
  }

export const reducer = (state=initialState,action)=>{

    switch(action.type){
        case HYDRATE:
            console.log('HYDRATE',action)
            return{...state,...action.payload}
            case LOG_IN_REQUEST:
                return {
                       ...state,
                       loginLoading:true,
                       loginError:null

                };
            case LOG_IN_SUCCESS:
                return {
                       ...state,
                       loginLoading:false,
                       loginDone:true,
                       me:dummyUser(action.data)
                    //    {...action.data,nickname:'dongzero'}
                };
            case LOG_IN_FAILURE:
                    return {

                           ...state,
                           loginLoading:false,
                           loginDone:false,
                           loginError:action.err
                    };


            case LOG_OUT_REQUEST:
                return {
                        ...state,
                        logoutLoading:true,

                }  
                case LOG_OUT_SUCCESS:
                    return {
                            ...state,
                            logoutLoading:false,
                            logoutDone:true,
                            me:null
                    }  
                    case LOG_OUT_FAILURE:
                        return {
                                ...state,
                                logoutDone:false,
                                logoutLoading:false,
                                logoutError:action.err,
                        }  
            case SIGN_UP_REQUEST:
                return{
                    ...state,
                    signupLoading:true,
                    signupDone:false,
                    signupError:null

                }
            case SIGN_UP_SUCCESS:
                return{
                    ...state,

                    signupLoading:false,
                    signupDone:true,

                }
            case SIGN_UP_FAILURE:
                return{
                    ...state,
                    signupError:action.err,
                    signupLoading:false,
                    signupDone:false,
                }


                case NICKNAME_REQUEST:
                    return{
                        ...state,
                        nicknameLoading:true,
                        nicknameDone:false,
                        nicknameError:null

                    }
                case NICKNAME_SUCCESS:
                    return{
                        ...state,

                        nicknameLoading:false,
                        nicknameDone:true,

                    }
                case NICKNAME_FAILURE:
                    return{
                        ...state,
                        nicknameError:action.err,
                        nicknameLoading:false,
                        nicknameDone:false,
                    }
                case ADD_POST_TO_ME:
                    return{
                        ...state,
                        me:{
                            Posts:[{id:action.data},...state.me.Posts],
                            ...state.me
                        }
                    }


        default:
            return state;
    }

}

export default reducer
